<div class="weather_cnt rates_cnt min_hgt" data-scroll-index='1' >    <div class="bnr_cnt" ng-controller="ratesController">        <div class="mid_cnt">            <div class="bnr_txt ">                <h3>Rates</h3>            </div>            <form class="form-horizontal col-sm-10">                <div class="form-group " ng-if="!selected.State && states">                    <select class="form-control" ng-options="state.State as state.State for state in states"                            ng-change="getDistricts()" ng-model="selected.State" >                        <option value="">Select State</option>                    </select>                </div>                <div class="form-group " ng-if="districts.length && !selected.District">                    <select class="form-control "                            ng-options="state.District as state.District for state in districts"                            ng-change="getMarkets()" ng-model="selected.District">                        <option value="">Select District</option>                    </select>                </div>                <div class="form-group " ng-if="markets.length && !selected.Market">                    <select class="form-control " ng-options="state.Market as state.Market for state in markets"                            ng-change="getCommodities()" ng-model="selected.Market" >                        <option value="">Select Market</option>                    </select>                </div>                <div class="form-group" ng-if="commodities.length">                    <select class="form-control "                            ng-options="state.Commodity as state.Commodity for state in commodities" ng-change="getRates()"                            ng-model="selected.Commodity">                        <option value="">Select Commodity</option>                    </select>                </div>            </form>            <div class="col-sm-2"> <a href class="btn btn-default btn-app" ng-click="reset()"><i class="glyphicon glyphicon-refresh"></i>Reset</a></div><div class="clearfix"></div>        <table ng-table="tableParams" class="table table-responsive table-bordered" show-filter="false" ng-show="rates.length">            <tr ng-repeat="rate in $data">                <td title="'Variety'" filter="{ Variety: 'text'}" sortable="'Variety'">                    {{rate.Variety}}                </td>                <td title="'Arrival Date'" filter="{ Variety: 'text'}" sortable="'Arrival_Date'">                    {{rate.Arrival_Date}}                </td>                <td title="'Min Price'" filter="{ Min_x0020_Price: 'number'}" sortable="'Min_x0020_Price'">                    {{rate.Min_x0020_Price}}                </td>                <td title="'Max Price'" filter="{ Max_x0020_Price: 'number'}" sortable="'Max_x0020_Price'">                    {{rate.Max_x0020_Price}}                </td>                <td title="'Modal Price'" filter="{ Modal_x0020_Price: 'number'}" sortable="'Modal_x0020_Price'">                    {{rate.Modal_x0020_Price}}                </td>            </tr>        </table>        </div>    </div></div>